  return (
    <div className="container-fluid vh-100 d-flex flex-column justify-content-center align-items-center bg-light">
      <div className="text-center mb-4">
        <img src={logo} alt="GestioPro Logo" className="mb-3" style={{ height: '60px' }} />
        <h1 className="h3">GestioPro</h1>
      </div>
      <h2 className="text-center mb-4">Bienvenue sur votre application de gestion d'entreprise</h2>
      <p className="text-center mb-4">Optimisez la gestion de votre entreprise avec notre solution compl√®te et intuitive.</p>
      <Link to="/login" className="btn btn-primary">Commencer maintenant</Link>
    </div>
  );
};

export default WelcomeBanner;
