import React from 'react';
import { Link } from 'react-router-dom';
import './WelcomeBanner.css'; // Nous allons créer ce fichier CSS

const WelcomeBanner = () => {
  return (
    <div className="welcome-container">
      <header className="welcome-header">
        <h1>GestioPro</h1>
        <Link to="/login" className="login-button">Connexion</Link>
      </header>
      <main className="welcome-content">
        <h2>Bienvenue sur votre application de gestion d'entreprise</h2>
        <p>Optimisez la gestion de votre entreprise avec notre solution complète et intuitive.</p>
        <Link to="/login" className="cta-button">Commencer maintenant</Link>
      </main>
    </div>
  );
};

export default WelcomeBanner;
