import React from 'react';
import { Link } from 'react-router-dom';
import './WelcomeBanner.css';
import logo from '../assets/logo.png'; // Assurez-vous que le chemin est correct

const WelcomeBanner = () => {
  return (
    <div className="welcome-container">
      <header className="welcome-header">
        <div className="logo-container">
          <img src={logo} alt="GestioPro Logo" className="logo" />
          <h1>GestioPro</h1>
        </div>
        <Link to="/login" className="login-button">Connexion</Link>
      </header>
      <main className="welcome-content">
        <h2>Bienvenue sur votre application de gestion d'entreprise</h2>
        <p>Optimisez la gestion de votre entreprise avec notre solution compl√®te et intuitive.</p>
        <Link to="/login" className="cta-button">Commencer maintenant</Link>
      </main>
    </div>
  );
};

export default WelcomeBanner;
